    confirm: function confirm() {
      var _this = this;

      //判断当前为编辑还是新增
      if (this.operateType === "edit") {
        //更新table数组
        this.$http.post("/user/edit", this.operateForm).then(function (res) {
          console.log("编辑" + res);
          _this.isShow = false;

          _this.getList();
        });
      } else {
        this.$http.post("/user/add", this.operateForm).then(function (res) {
          console.log("新增" + res);
          _this.isShow = false;

          _this.getList();
        });
      }
    },
    addUser: function addUser() {
      this.isShow = true;
      this.operateType = "add";
      this.operateForm = {
        name: "",
        addr: "",
        age: "",
        birth: "",
        sex: ""
      };
    },
    editUser: function editUser(row) {
      this.operateType = "edit";
      this.isShow = true;
      this.operateForm = row;
    },
