        //弹窗类型
        type: "warning"
      }).then(function () {
        var id = row.id;

        _this2.$http.get("/user/del", {
          params: {
            id: id
          }
        }).then(function () {
          _this2.$message({
            type: "success",
            message: "删除成功"
          });
        });

        _this2.getList();
      });
    },
