import { getData } from "../../../api/data";
import Echart from "components/Echarts.vue";
export default {
  name: "Home",
  components: {
    Echart: Echart
  },
  data: function data() {
    return {
      userImg: require("assets/images/user.png"),
      tableData: [],
      tableLabel: {
        name: "手机型号",
        todayBuy: "今日购买",
        monthBuy: "本月购买",
        totalBuy: "总购买"
      },
      countData: [{
        name: "今日支付订单",
        value: 1234,
        icon: "success",
        color: "#2ec7c9"
      }, {
        name: "今日收藏订单",
        value: 210,
        icon: "star-on",
        color: "#ffb980"
      }, {
        name: "今日未支付订单",
        value: 1234,
        icon: "s-goods",
        color: "#5ab1ef"
      }, {
        name: "本月支付订单",
        value: 12234,
        icon: "success",
        color: "#2ec7c9"
      }, {
        name: "本月收藏订单",
        value: 4555,
        icon: "star-on",
        color: "#ffb980"
      }, {
        name: "本月未支付订单",
        value: 233,
        icon: "s-goods",
        color: "#5ab1ef"
      }],
      echartData: {
        order: {
          xData: [],
          series: []
        },
        user: {
          xData: [],
          series: []
        },
        video: {
          series: []
        }
      }
    };
  },
  mounted: function mounted() {
    var _this = this;

    getData().then(function (res) {
      var code = res.data.code;
      var data = res.data.data;

      if (code === 20000) {
        _this.tableData = data.tableData; //订单----折线图

        var order = data.orderData;
        var xData = order.date;
        var keyArray = Object.keys(order.data[0]);
        var series = [];
        keyArray.forEach(function (key) {
          series.push({
            name: key,
            type: "line",
            data: order.data.map(function (item) {
              return item[key];
            })
          });
        });
        _this.echartData.order.xData = xData;
        _this.echartData.order.series = series; //用户图----柱状图

        _this.echartData.user.xData = data.userData.map(function (item) {
          return item.date;
        });
        _this.echartData.user.series = [{
          name: "新增用户",
          data: data.userData.map(function (item) {
            return item.new;
          }),
          type: "bar"
        }, {
          name: "活跃用户",
          data: data.userData.map(function (item) {
            return item.active;
          }),
          type: "bar"
        }]; //饼图

        _this.echartData.video.series = [{
          data: data.videoData,
          type: "pie"
        }];
      }
    });
  }
};
