import Mock from 'mockjs'

import urlConfig from "@/utils/urlConfig";

import homeHttp from './mockServeData/home'
import userApi from './mockServeData/user'
import permissionApi from './mockServeData/permission'

// 首页数据
Mock.mock(urlConfig.home.getHomeData,homeHttp.getStatisticalData)
// 添加用户
Mock.mock(urlConfig.user.addUser, userApi.updateUser)
// 编辑用户
Mock.mock(urlConfig.user.editUser, userApi.updateUser)
// 获取用户列表
Mock.mock(urlConfig.user.getUserList, userApi.getUserList)
// 删除用户
Mock.mock(urlConfig.user.delUser, userApi.deleteUser)
// 根据登录账户获取路由
Mock.mock(urlConfig.login.getMenu,permissionApi.getMenu)
